import { IReceita } from './../../../model/IReceita.model';
import { ReceitasService } from './../../../services/receitas.service';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-listar-receitas',
  templateUrl: './listar-receitas.component.html',
  styleUrls: ['./listar-receitas.component.css'],
})
export class ListarReceitasComponent implements OnInit {
  listaReceitas: IReceita[] = [];

  constructor(private receitasService: ReceitasService) {}

  ngOnInit(): void {
    this.carregarReceitas();
  }

  carregarReceitas(): void {
    this.receitasService.buscarTodos().subscribe(retorno =>{
      this.listaReceitas = retorno;
    })
  }

}
<h1 class="my-3 text-primary">
  Listar Receitas
  <button
    type="buttom"
    routerLink="/receitas/"
    class="btn btn-success mt-2 float-right"
  >
    Cadastrar
  </button>
</h1>
<div class="card shadow">
  <table class="table table-responsive table striped">
    <thead>
      <tr>
        <th>#</th>
        <th>Nome</th>
        <th>Validade</th>
        <th>Valor</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let rec of listaReceitas">
        <td>{{ rec.id }}</td>
        <td>{{ rec.nome }}</td>
        <td>{{ rec.validade | date: "shortDate" }}</td>
        <td>{{ rec.precoProduto | currency: "BRL" }}</td>
        <td>
          <button class="btn btn-warning">
            <i class="fa fa-pencil" aria-hidden="true"></i>
          </button>
          <button class="btn btn-danger ml-2">
            <i class="fa fa-trash-o" aria-hidden="true"></i>
          </button>
        </td>
      </tr>
    </tbody>
  </table>
</div>
